"use strict";
// Data Table Untuk recent
$.ajaxSetup({
    headers: {
        'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
    }
});

$(document).ready(function () {
    $("#table-recent").DataTable({
        columnDefs: [{
            sortable: true,
            targets: [1]
        }],
        lengthChange: true,
        order: [
            [1, "asc"]
        ],
        processing: true,
        serverSide: true,
        orderable: true,
        searchable: true,
        responsive: true,
        ajax: "recent",
        columns: [{
                data: "DT_RowIndex",
                name: "DT_RowIndex",
                orderable: false,
                searchable: false
            },
            {
                data: "kategori_recent_work",
                name: "kategori_recent_work"
            },
            {
                data: "name_recent_work",
                name: "name_recent_work"
            },
            {
                data: "desc",
                name: "desc"
            },
            {
                data: "images",
                name: "images"
            },
            {
                data: "action",
                name: "action",
                orderable: false,
                searchable: false
            }
        ]
    });
});

$(document).on("click", "button.confirmDeleteRecent", function (e) {
    e.preventDefault();
    var id = $(this).attr("data-id");
    Swal.fire({
            title: 'Are you sure?',
            text: "You won't be able to revert this!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!',
            showClass: {
                popup: 'animated bounceIn faster'
            },
            hideClass: {
                popup: 'animated bounceOut faster'
            }
        })
        .then((result) => {
            if (result.value) {
                $.ajax({
                    url: "recent/" + id,
                    type: "POST",
                    data: {
                        '_method': 'DELETE'
                    },
                    success: function (data) {
                        Swal.fire({
                                title: data.title,
                                text: data.text,
                                icon: data.icon
                            })
                            .then(() => {
                                location.reload();
                            });
                    },
                    error: function () {
                        Swal.fire({
                            title: 'Opps...',
                            text: data.message,
                            type: 'error',
                            timer: '1500'
                        })
                    }
                })
            }
        });
});
// End Data Table Untuk recent